import React from "react";
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom'; 

import NewProduct from "../Product/NewProduct/NewProduct";
import NavBar from "../../UI/NavBar/NavBar";

import styles from './AdminDashboard.module.css';

const AdminDashboard = (props) =>{


    const onClickButton = (event) => {
        let li_elements = document.querySelectorAll('.menu-item')
        for(let x = 0; x < li_elements.length; x++) {
        li_elements[x].classList.remove(`${styles.active}`)
        }
        event.target.classList.add(`${styles.active}`);
    }

return(   
    <Router>
        <React.Fragment>
            <div className={`${styles.container}`}>
           <NavBar/>
            <Switch>
                <Route exact path="/">
                    <NewProduct/>
                </Route>
                <Route path="/manage-inventory">
                    <NewProduct/>
                </Route>
                <Route path="/manage-payments">
                    <NewProduct/>
                </Route>
                <Route path="/manage-accounts">
                    <NewProduct/>
                </Route>
                <Route path="/manage-accounts">
                    <NewProduct/>
                </Route>
            </Switch>
            </div>
        </React.Fragment>
    </Router>
)
}

export default AdminDashboard;
