import React, { useEffect, useState }  from "react";
import Axios from "axios";
// import { useSelector, useDispatch } from 'react-redux'


import styles from './Payment.module.css';

const Payment = (props) =>{
    const [userdetail, setUserDetail] = useState({});
    const [fromdate, setFromDate] = useState();
    const [todate, setToDate] = useState();

    const userIdGetter = async (object) => {
        let result = await Axios.post("http://localhost:3001/selectuser", {
                            userid: object
                        });
        if(result.data === null){
            window.alert("User Not Found!");
        }
        else{

            setUserDetail(result)
        }
    }


    return( 
            <React.Fragment>
                <div className={`${styles.container}`}> 
                    <ul className={`${styles.ul}`}>
                        <li className={`${styles.input}`}>
                            <label>Customer Name or ID</label>
                            <input onKeyDown={(e)=>{
                                if(e.code == "Enter"){
                                    userIdGetter(e.target.value);
                                }
                            }}/>
                        </li>
                        <li className={`${styles.input}`}>
                            <label>Date from</label>
                            <input type="date" id="datefrom" name="datefrom"></input>
                        </li>
                        <li className={`${styles.input}`}>
                            <label>Date from</label>
                            <input type="date" id="dateto" name="dateto" onChange={null}></input>
                        </li>
                    </ul>
                    <table className={`${styles.customers}`}>
                        <tr>
                            <th>ID #: {userdetail.data.userid}</th>
                            <th>Customer: {userdetail.data.fn} {userdetail.data.ln}</th>
                            <th>Amount Payable</th>
                        </tr>
                    </table>
                </div>
            </React.Fragment>
            )
}

export default Payment;
