import React, {useState, useEffect} from "react";
import styles from './UpdateProduct.module.css';
import Axios from "axios";
import { Formik, Form, Field, ErrorMessage, useFormik } from 'formik';
import { useHistory } from 'react-router-dom'; 
import { AddProductSchema } from "../../../Schemas/Schema";
import ProductFilter from "../ViewProducts/ProductFilter";
import { createRef } from "react";


const UpdateProduct = () => {
    const history = useHistory();
    const [searchProduct, setSearchProduct] = useState('');
    const [products, getProducts] = useState([]);
    const [productId, setProductId] = useState('');
    const [productName, setProductName] = useState('');
    const [productPrice, setProductPrice] = useState('');

    useEffect(()=>{
        myfunction()
        // Axios.get('http://localhost:3001/api/get').then((response)=>{
        //     getProducts(response.data);
        // })
        // return function cleanup() {
        //   };
    },[]);

    const myfunction = async () => {
        let result = await Axios.get('http://localhost:3001/api/get')
        getProducts(result.data);
      }

    const updateProduct = async () =>{
         let result = await  Axios.post("http://localhost:3001/api/update-product", {
            productName: productName, 
            price: productPrice,
            productId: productId,
        })
    }

    const cancel = () => {
        history.push('/dashboard');
    }

    const filteredProductArray = products.filter(product => product.productName.includes(searchProduct.toLowerCase()));

    const filteredProduct = (event) => {
        setSearchProduct(event.target.value);

        if(filteredProductArray.length === 0){
            setProductName('');
            setProductPrice('');
        }else{
            console.log(filteredProductArray[0].productName.charAt(0).toUpperCase()+filteredProductArray[0].productName.slice(1))
            document.getElementById('productname').value = filteredProductArray[0].productName.charAt(0).toUpperCase()+filteredProductArray[0].productName.slice(1)
            // setProductName(filteredProductArray[0].productName.charAt(0).toUpperCase()+filteredProductArray[0].productName.slice(1));
            // setProductPrice(filteredProductArray[0].price);
            // setProductId(filteredProductArray[0].productId);
            // setFieldValue()
        }
    }

    const formik = useFormik({
        initialValues: { productName: '', price: ''},
        validationSchema:AddProductSchema,
        validateOnBlur:false,
        validateOnMount:false,
        validateOnChange:false,
        onSubmit: (values, {setSubmitting, resetForm}) => {
          alert(JSON.stringify(values, null, 2));

            // setTimeout(() => {
            //     updateProduct()
            //     window.alert("Product Updated!")
            //     resetForm({values: ''});
            //     setProductName('');
            //     setProductPrice('');
            //     setSubmitting(false);
            // }, 100);
        },
      });

    return(
        <div className={`${styles.container}`}> 
            <ProductFilter filteredProduct={filteredProduct}/>
            <div className={styles.center}>
                <h1 className={styles.h1}>Update Product</h1>
                {/* <Formik
                    initialValues={{ productName: '', price: ''}}
                    validationSchema={AddProductSchema}
                    validateOnBlur={false}
                    validateOnMount={false}
                    validateOnChange={false}
                    onSubmit={(values, {setSubmitting, resetForm}) => {

                        setTimeout(() => {
                            updateProduct()
                             window.alert("Product Updated!")
                            resetForm({values: ''});
                            setProductName('');
                            setProductPrice('');
                            setSubmitting(false);
                        }, 100);
                    }}
                > */}
                    <form onSubmit={formik.handleSubmit}>
                        <label className={styles.label}>Product Name</label>
                        <input
                        onChange={(event) => {setProductName(event.target.value.toLowerCase())}} 
                        name="productName"
                        type="text"
                        id="productname" 
                        className={`${styles.input}`}/>
                        {
                        formik.errors.productName && formik.touched.productName ? (<div className={styles.error}>{formik.errors.productName}</div>) : null
                        }
                         <label className={styles.label}>Product Price</label>
                        <input
                        onChange={(event) => {setProductPrice(event.target.value)}} 
                        type="text" 
                        name="price" 
                        id="price"
                        className={`${styles.input}`}/>
                        {
                        formik.errors.price && formik.touched.price ? (<div className={styles.error}>{formik.errors.price}</div>) : null
                        }
                        <button onSubmit={null} type="submit"  className={`${styles.button}`}>
                            Update
                        </button>
                        <button onClick={()=>cancel()}  type="submit" className={`${styles.button} ${styles.red}`}>
                            Cancel
                        </button>
                    </form>


                {/* </Formik> */}
            </div>
        </div>
    )
}

export default UpdateProduct;
