import './App.css';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';

import ProductList from './Components/Product/ViewProducts/ProductList';
import Login from './Components/Login/Login';
import Register from './Components/Register/Register';
import React, { useState }  from 'react';
import AdminDashboard from './Components/AdminDashboard/AdminDashboard';
import ProtectedRoute from './Components/Route/ProtectedRoute';


function App() {
  const [Auth, setIsTrue] = useState(false);

  useEffect( async () => {

    let result = await Axios.get("http://localhost:3001/isUserAuth", 
    {headers: {"x-access-token": localStorage.getItem("token")}});
    console.log(result.data.Auth)
    setIsTrue(result.data.Auth)

  },[])

  return (
    <Router>
      <Switch>
      <React.Fragment>
          <Route listeners={(evt) => console.log('listening',evt)} exact path="/login" component={Login}/>
          <Route listeners={(evt) => console.log('listening',evt)} exact path={["/register"]} component={Register}/>
          <Route listeners={(evt) => console.log('listening',evt)} exact path={["/", "/baligya"]} component={ProductList}/>
          <ProtectedRoute path={"/dashboard"}  isAuth={false} component={AdminDashboard} isAuth={false}/>
      </React.Fragment>
      </Switch>
    </Router>
  );
 }

export default App;
