import React, { useState } from "react";
import Axios from "axios";
import { Formik, Form, Field, ErrorMessage } from 'formik';
import { AddProductSchema } from "../../../Schemas/Schema";

import styles from './NewProduct.module.css';

const NewProduct = () =>{

    const [productName, setProductName] = useState('');
    const [productPrice, setProductPrice] = useState('');

    const addProduct = () =>{
        Axios.post("http://localhost:3001/api/insert", {
            productName: productName, 
            price: productPrice
        }).then(()=>{
                alert("successful insert");
            });
    }

    return( 
        <div className={`${styles.container}`}> 
            <Formik
                initialValues={{ productName: '', price: ''}}
                validationSchema={AddProductSchema}
                validateOnBlur={false}
                validateOnChange={false}
                onSubmit={async (values, {setSubmitting, resetForm}) => {
                    setTimeout(async () => {
                        let result = await  Axios.post("http://localhost:3001/api/insert", {
                            productName: productName, 
                            price: productPrice
                        })
                        setSubmitting(false);
                    }, 100);
                }}
            ></Formik>

            {/* <form>
                <label>Product Name</label>
                <input 
                    type="text" 
                    name="productName" 
                    onChange={(event) => {setProductName(event.target.value.toLowerCase())}}
                />
                <label>Product Price</label>
                <input 
                    type="text"
                    name="productPrice" 
                    onChange={(event) => {setProductPrice(event.target.value.toLowerCase())}}
                />
                <Button onClick={addProduct}>Add Product</Button>
            </form> */}
        </div>
    )
}

export default NewProduct;
