import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

import styles from './NavBar.module.css';

const NavBar = (props) => {
    const [pageState, setPageState] = useState('manage-products');

    useEffect(() => {
        //imong custom function na mu set ug active menu dpende sa page
       // e.g setMenuActive('products')
         let ac = document.getElementById(pageState);
         console.log(window.location.pathname);
        setMenuActive(ac);
     },[pageState])

     const setMenuActive = (menu) => {
        if ( menu.id === 'manage-products') {
            menu.classList.add(`${styles.active}`);
          //imong algorithm diri para ma set ang menu product as active
        }
     }

    const onClickButton = (event) => {
        let li_elements = document.querySelectorAll('.menu-item')
        for(let x = 0; x < li_elements.length; x++) {
        li_elements[x].classList.remove(`${styles.active}`)
        }
        event.target.classList.add(`${styles.active}`);
    }

    return(
            <ul className={`${styles.sidenav}`}>
                <Link to="/" style={{ textDecoration: 'none' }}>
                    <li className={`${styles.li} menu-item`} onClick={(event) => onClickButton(event)} id="manage-products">
                    Manage Products
                    </li>
                </Link>
                <Link to="/manage-inventory" style={{ textDecoration: 'none' }}>
                    <li className={`${styles.li} menu-item`} onClick={(event) => onClickButton(event)} id="manage-inventory">
                    Manage Inventory
                    </li>
                </Link>
                <Link to="/manage-payments" style={{ textDecoration: 'none' }} id="manage-payments">
                    <li className={`${styles.li} menu-item`} onClick={(event) => onClickButton(event)}>
                    Manage Payments
                    </li>
                </Link>
                <Link to="/manage-acccounts" style={{ textDecoration: 'none' }} id="manage-accounts"> 
                    <li className={`${styles.li} menu-item`} onClick={(event) => onClickButton(event)}>
                    Manage Accounts
                        </li>
                </Link>
                <li className={`${styles.li} menu-item`} onClick={(event) => onClickButton(event)}>Logout</li>
            </ul>
    );
}

export default NavBar;
