import React, { useState } from "react";
import { Formik, Form, Field, ErrorMessage } from 'formik';
import Axios from "axios";

import { LoginSchema } from "../../Schemas/Schema";
import styles from './Login.module.css';


const Login = (props) =>{

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');



    // const sendGetRequest = async () =>{
    //     try {
    //         const resp = await Axios.post("http://localhost:3001/login", {
    //             username: username,
    //             password: password
    //         });
    //         console.log(resp.data);
    //     } catch (err) {
    //         // Handle Error Here
    //         console.error(err);
    //     }
    // };

    return(
        <div className={styles.div}>
            <h4 style={{textAlign: 'center'}}>Welcome to Verna Store. Please Login.</h4>
            <Formik
             initialValues={{ username: '', password: ''}}
             validationSchema={LoginSchema}
             validateOnBlur={false}
             validateOnChange={false}
             onSubmit={(values, {setSubmitting, resetForm}) => {
                 if(values){
                    setTimeout(async () => {
                        let result = await Axios.post("http://localhost:3001/login", {
                            username: username,
                            password: password
                        }, {withCredentials: true});
                        console.log(result)
                        // sendGetRequest()
                    setSubmitting(false);
                    }, 100);
                 }else{
                    setTimeout(async () => {
                        let result = await Axios.post("http://localhost:3001/profile");
                        console.log(result)
                        // sendGetRequest()
                    setSubmitting(false);
                    }, 100);
                 }

            }}
            >
            {({isSubmitting, errors, touched})=>(
                <Form className={styles.form}>
                    <label className={`${styles.label}`}>Username</label>
                    <Field onKeyUp={(event) => {setUsername(event.target.value)}} type="text" name="username" className={`${styles.input}`}/>
                    {
                     errors.username && touched.username ? (<div>{errors.username}</div>) : null
                    }
                    <label className={`${styles.label}`}>Password</label>
                    <Field onKeyUp={(event) => {setPassword(event.target.value)}} type="password" name="password" className={`${styles.input}`}/>
                    {
                     errors.password && touched.password ? (<div>{errors.password}</div>) : null
                    }
                    <button onSubmit={true} type="submit" disabled={isSubmitting} className={`${styles.button}`}>
                        Submit
                    </button>
                    <button onSubmit={false} type="submit" disabled={isSubmitting} className={`${styles.button}`}>
                        Submit
                    </button>
                </Form>
            )}
            </Formik>
        </div>
    );

}

export default Login;
