import React, { useState }  from "react";
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom'; 
import { Redirect, withRouter } from "react-router";
import { useEffect } from "react";

import Product from "../Product/Product";
import Inventory from "../Inventory/Inventory";
import Payment from "../Payment/Payment";
import Account from "../Account/Account";
import NavBar from "../../UI/NavBar/NavBar";

import styles from './AdminDashboard.module.css';

const AdminDashboard = (props, {Authorized}) =>{

    const [Auth, setIsTrue] = useState(false)

    useEffect( async () => {
      let result = await Axios.get("http://localhost:3001/isUserAuth", 
      {headers: {"x-access-token": localStorage.getItem("token")}});

      setIsTrue(result.data)
    },[])

    if(Authorized || Auth){
        return(   
            <div className={`${styles.container}`}>
            <Router>
            <NavBar/>
                <Switch>
                    <Route exact path="/manage-product">
                        <Product/>
                    </Route>
                    <Route exact path="/manage-inventory">
                        <Inventory/>
                    </Route>
                    <Route exact path="/manage-payments">
                        <Payment/>
                    </Route>
                    <Route exact path="/manage-accounts">
                        <Account/>
                    </Route>
                </Switch>
            </Router>
            </div>
        )
    }else{
        return <Redirect to={{pathname: '/login', state:{from:props.location}}}/>;
    }

}

export default withRouter(AdminDashboard);
