import React, { useState } from "react";
import Card from "../../UI/Card/Card";
import ProductItem from "./ProductItem";
import ProductFilter from "./ProductFilter";

const ProductList = (props) =>{
    const [searchProduct, setSearchProduct] = useState('');

    const filteredProductArray = props.products.filter(product => product.prod_name.includes(searchProduct.toLowerCase()));
    
    const filteredProduct = (event) =>{
        setSearchProduct(event.target.value);
        
        console.log(searchProduct);
    }
    
    return(
        <div>
        <ProductFilter filteredProduct={filteredProduct}/>
        <ul>{props.products.filter(product => product.prod_name.includes(searchProduct.toLowerCase())).map((product) =>
            <li style={{display: 'inline-block', marginRight: '5rem'}} key={product.id}>
                <ProductItem 
                    productName={product.prod_name} 
                    price={product.price} 
                    stock={product.stock}/>
            </li>
        )}
        </ul>
        </div>
    );
}

export default ProductList;
