import React, { useState } from "react";
import styles from './ViewCart.module.css';
import cart from '../../img/trolley.png';

const ViewCart = () => {
    const [view, setView] = useState(false);

    var elem = document.getElementById('viewcart');
    elem.removeEventListener('click', false);
    return(
        <React.Fragment>
            {!view 
            ? 
                <div className={`${styles.container}`}>
                    <img src={cart} alt='cart' className={`${styles.cart}`} onClick={()=>{setView(true);}}/> 
                </div>
            : 
                <div className={`${styles.overlay}`} onClick={()=>{setView(false);}}>
                    <div className={`${styles.viewcart}`} id='viewcart'>
                        Check
                    </div>
                </div>
            }
        </React.Fragment>
    )
}

export default ViewCart;
