import React, { useState } from "react";
import { Formik, Form, Field, ErrorMessage } from 'formik';
import Axios from "axios";
import * as Yup from 'yup';

import styles from './Register.module.css';

const Register = (props) => {

    const [username1, setUsername] = useState('');
    const [password1, setPassword] = useState('');
    const [cpassword1, setCPassword] = useState('');

    let RegisterSchema = Yup.object().shape({
        username: Yup.string().min(5, "Username must be 5 characters long").required('Required Username')
        .test("is_exists", `${username1} is not available`, (value)=>{
            return new Promise( async (resolve, reject)=>{
                let result = await Axios.post("http://localhost:3001/checkusername", {
                    username: username1,
                    password: password1
                    }, {
                        headers: {'Content-Type': 'application/json'}
                    })

            if(value === result.data.username){
                resolve(false)
            }
            else{
                resolve(true)
            }
            })
        }),
        password: Yup.string().min(6, "Must be 6 or more characters").required('Required Password'),
        cpassword: Yup.string()
        .oneOf([Yup.ref('password'), null], 'Passwords must match').required('Required Confirm Password')
    });

    const sendPostRequest = async () => {
        try{
        let result = await Axios.post("http://localhost:3001/register", {
        username: username1,
        password: password1
        }, {
            headers: {'Content-Type': 'application/json'}
        });
        }catch(err){
            console.log(err);
        }
    };

    return(
        <div className={styles.div}>
            <h4 style={{textAlign: 'center'}}>Register</h4>
            <Formik
                initialValues={{ username: '', password: '', cpassword:'' }}
                validationSchema={RegisterSchema}
                onSubmit={(values, {setSubmitting, resetForm}) => {
                    setTimeout(() => {
                    sendPostRequest().then(res =>{
                        setPassword('');
                            setUsername('');
                            resetForm({values: ''});
                    })
                    setSubmitting(false);
                    }, 100);
                }}
                >
                {({ isSubmitting, errors, touched }) => (
                    <Form className={styles.form}>
                        <label className={`${styles.label}`}>Username</label>
                    <Field onKeyUp={(event) => {setUsername(event.target.value)}} type="text" name="username" className={`${styles.input}`}/>
                    {/* <ErrorMessage name="username" component="div" /> */}
                    {
                     errors.username && touched.username ? (<div>{errors.username}</div>) : null
                    }
                    <label className={`${styles.label}`}>Password</label>
                    <Field onKeyUp={(event) => {setPassword(event.target.value)}} type="password" name="password" className={`${styles.input}`}/>
                    {
                     errors.password && touched.password ? (<div>{errors.password}</div>) : null
                    }
                    <label className={`${styles.label}`}>Confirm Password</label>
                    <Field onKeyUp={(event) => {setCPassword(event.target.value)}} type="password" name="cpassword" className={`${styles.input}`}/>
                    {
                     errors.cpassword && touched.cpassword ? (<div>{errors.cpassword}</div>) : null
                    }
                    <button onSubmit={null} type="submit" disabled={isSubmitting} className={`${styles.button}`}>
                        Submit
                    </button>
                    </Form>
                )}
            </Formik>
        </div>
    );
}

export default Register;
