import React, { useState } from "react";
import { Formik, Form, Field, ErrorMessage } from 'formik';
import Axios from "axios";

import styles from './Register.module.css';

const Register = (props) => {

    const [username1, setUsername] = useState('');
    const [password1, setPassword] = useState('');
    const [cpassword1, setCPassword] = useState('');

    const sendPostRequest = async () => {
        let result = await Axios.post("http://localhost:3001/register", {
        username: username1,
        password: password1
      }, {
        headers: {'Content-Type': 'application/json'}
      });
        if(result.data === 1){
            alert(JSON.stringify("Username Exists!"));
            return 0;
        }else{
            alert(JSON.stringify("Registered!"));
            return 1;
        }


        // Axios.post("http://localhost:3001/register", {
        //     username: username1,
        //     password: password1,
        // }).then(()=>{
        //         alert("successful insert");
        //     });
        // try {
        //     await Axios.post("http://localhost:3001/register", {
        //         username: username,
        //         password: password,
        //     }).then(()=>{
        //         alert("Registered!");
        //     });
        //     // console.log(resp.data);
        // }catch (err) {
        //     // Handle Error Here
        //     console.error(err);
        // }
    };

    return(
        <div className={styles.div}>
            <h4 style={{textAlign: 'center'}}>Register</h4>
            <Formik
                initialValues={{ email: '', password: '', cpassword:'' }}
                validate={values => {
                    const errors = {};
                    if (!values.email) {
                    errors.email = 'Required';
                    } else if (
                    !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
                    ) {
                    errors.email = 'Invalid email address';
                    }
                    if(!values.password){
                        errors.password = 'Required';
                    }
                    if(values.password.length < 5){
                        errors.password = 'Password too short!';
                    }
                    if(values.password.length === 0){
                        errors.password = '';
                    }
                    if(values.cpassword !== values.password){
                        errors.cpassword = 'Password and Confirm Password did not match';
                    }
                    return errors;
                }}
                onSubmit={(values, { setSubmitting, resetForm }) => {
                    setTimeout(() => {
                    let x = sendPostRequest();
                    if(x === 1){
                        resetForm({values:''});
                    }
                    setSubmitting(false);
                    }, 400);
                }}
                >
                {({ isSubmitting }) => (
                    <Form className={styles.form}>
                        <label className={`${styles.label}`}>Username</label>
                    <Field onKeyUp={(event) => {setUsername(event.target.value)}} type="email" name="email" className={`${styles.input}`}/>
                    <ErrorMessage name="email" component="div" />
                    <label className={`${styles.label}`}>Password</label>
                    <Field onKeyUp={(event) => {setPassword(event.target.value)}} type="password" name="password" className={`${styles.input}`}/>
                    <ErrorMessage name="password" component="div" />
                    <label className={`${styles.label}`}>Confirm Password</label>
                    <Field onKeyUp={(event) => {setCPassword(event.target.value)}} type="password" name="cpassword" className={`${styles.input}`}/>
                    <ErrorMessage name="cpassword" component="div" />
                    <button onSubmit={sendPostRequest} type="submit" disabled={isSubmitting} className={`${styles.button}`}>
                        Submit
                    </button>
                    </Form>
                )}
            </Formik>
        </div>
    );
}

export default Register;
