          <Route listeners={(evt) => console.log('listening',evt)} exact path={["/register"]} component={Register}/>
          <Route listeners={(evt) => console.log('listening',evt)} exact path={["/", "/baligya"]} component={ProductList}/>
          <Route listeners={(evt) => console.log('listening',evt)} exact path={["/dashboard"]} component={()=><AdminDashboard/>}/>
      </React.Fragment>
      </Switch>
    </Router>
  );
 }

export default App;
