import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";

import styles from './NavBar.module.css';

const NavBar = (props) => {
    const history = useHistory();

    const clearActive = (event) =>{
        let li_elements = document.querySelectorAll('.menu-item')
        for(let x = 0; x < li_elements.length; x++) {
            li_elements[x].classList.remove('.active')
        }
        event.target.classList.add(`${styles.active}`)

    }

    const manageProducts = (event) =>{
        clearActive(event);
        history.push("/");
    }
    const manageInventory = () =>{
        clearActive(event);
        history.push("/manage-inventory");
    }
    const managePayments = () =>{
        clearActive(event);
        history.push("/manage-payments");
    }
    const manageAccounts = () =>{
        clearActive(event);
        history.push("/manage-accounts");
    }

    return(
            <ul className={`${styles.sidenav}`}>
                    <li className={`${styles.li} menu-item`} onClick={(event) => (manageProducts(event))} id="manage-products">
                    Manage Products
                    </li>
                    <li className={`${styles.li} menu-item`} onClick={(event) => manageInventory(event)} id="manage-inventory">
                    Manage Inventory
                    </li>
                    <li className={`${styles.li} menu-item`} onClick={(event) => managePayments(event)} id="manage-payments">
                    Manage Payments
                    </li>
                    <li className={`${styles.li} menu-item`} onClick={(event) => manageAccounts(event)} id="manage-accounts">
                    Manage Accounts
                    </li>
            </ul>
    );
}

export default NavBar;
