import React from "react";
import NewProduct from "../NewProduct/NewProduct";


import styles from './AdminDashboard.module.css';

const AdminDashboard = (props) =>{


    const onClickButton = (event) => {
        let li_elements = document.querySelectorAll('.menu-item')
        for(let x = 0; x < li_elements.length; x++) {
        li_elements[x].classList.remove(`${styles.active}`)
        }
        event.target.classList.add(`${styles.active}`);
    }

return(   
    <React.Fragment>
        <div className={`${styles.container}`}>
        <ul className={`${styles.sidenav}`}>
            <li className={`${styles.li} menu-item`} onClick={(event) => onClickButton(event)}>Manage Products</li>
            <li className={`${styles.li} menu-item`} onClick={(event) => onClickButton(event)}>Manage Inventory</li>
            <li className={`${styles.li} menu-item`} onClick={(event) => onClickButton(event)}>Manage Payments</li>
            <li className={`${styles.li} menu-item`} onClick={(event) => onClickButton(event)}>Manage Accounts</li>
        </ul>
        
        <NewProduct/>
        </div>
    
    </React.Fragment>
)
}

export default AdminDashboard;
