import React, { useEffect, useState }  from "react";


import styles from './Account.module.css';

const Account = (props) =>{

    useEffect(() => {
        let ac = document.getElementById('manage-accounts');
        console.log(ac.classList);
        onAddClass(ac)
    },[])

    const onAddClass = (ac) => {
        let li_elements = document.querySelectorAll('.menu-item')
        for(let x = 0; x < li_elements.length; x++) {
        li_elements[x].classList.remove(`${styles.active}`)
        }
        ac.classList.add(`${styles.active}`);
    }


    return(
        <div className={`${styles.container}`}>
            Account
        </div>
    )
}

export default Account;
