import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";

import styles from './NavBar.module.css';

const NavBar = (props) => {
    const history = useHistory();

    const clearActive = () =>{
        let li_elements = document.querySelectorAll('.menu-item')
        for(let x = 0; x < li_elements.length; x++) {
            li_elements[x].classList.remove('.active')
        }
    }

    const manageProducts = () =>{
        clearActive();
        history.push("/");
    }
    const manageInventory = () =>{
        clearActive();
        history.push("/manage-inventory");
    }
    const managePayments = () =>{
        clearActive();
        history.push("/manage-payments");
    }
    const manageAccounts = () =>{
        clearActive();
        history.push("/manage-accounts");
    }

    return(
            <ul className={`${styles.sidenav}`}>
                    <li className={`${styles.li} menu-item`} onClick={manageProducts} id="manage-products">
                    Manage Products
                    </li>
                    <li className={`${styles.li} menu-item`} onClick={manageInventory} id="manage-inventory">
                    Manage Inventory
                    </li>
                    <li className={`${styles.li} menu-item`} onClick={managePayments} id="manage-payments">
                    Manage Payments
                    </li>
                    <li className={`${styles.li} menu-item`} onClick={manageAccounts} id="manage-accounts">
                    Manage Accounts
                    </li>
            </ul>
    );
}

export default NavBar;
