import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";

import styles from './NavBar.module.css';

const NavBar = (props) => {
    const history = useHistory();
    useEffect(() => {
        if(window.location.pathname === '/'){
            document.getElementById('manage-products').classList.add(`${styles.active}`)
        }else if(window.location.pathname === '/manage-inventory'){
            document.getElementById('manage-inventory').classList.add(`${styles.active}`)
        }else if(document.getElementById('manage-payments')){
            document.getElementById('manage-payments').classList.add(`${styles.active}`)
        }else if(document.getElementById('manage-accounts')){
            document.getElementById('manage-accounts').classList.add(`${styles.active}`)
        }
    },[])

    const clearActive = (event) =>{
        let li_elements = document.querySelectorAll('.menu-item')
        for(let x = 0; x < li_elements.length; x++) {
            li_elements[x].classList.remove(`${styles.active}`)
        }
        event.target.classList.add(`${styles.active}`)

    }

    const manageProducts = (event) =>{
        clearActive(event);
        history.push("/");
    }
    const manageInventory = (event) =>{
        clearActive(event);
        history.push("/manage-inventory");
    }
    const managePayments = (event) =>{
        clearActive(event);
        history.push("/manage-payments");
    }
    const manageAccounts = (event) =>{
        clearActive(event);
        history.push("/manage-accounts");
    }

    return(
            <ul className={`${styles.sidenav}`}>
                    <li className={`${styles.li} menu-item`} onClick={(event) => manageProducts(event)} id="manage-products">
                    Manage Products
                    </li>
                    <li className={`${styles.li} menu-item`} onClick={(event) => manageInventory(event)} id="manage-inventory">
                    Manage Inventory
                    </li>
                    <li className={`${styles.li} menu-item`} onClick={(event) => managePayments(event)} id="manage-payments">
                    Manage Payments
                    </li>
                    <li className={`${styles.li} menu-item`} onClick={(event) => manageAccounts(event)} id="manage-accounts">
                    Manage Accounts
                    </li>
            </ul>
    );
}

export default NavBar;
