import React from "react";
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
  } from "react-router-dom";

import styles from './NewProduct.module.css';

const NewProduct = (props) =>{


    const onClickButton = (event) => {

        console.log(event);
        let li_elements = document.querySelectorAll('.li')
      for(let x = 0; x < li_elements.length; x++) {
       li_elements[x].classList.remove('.active')
      }
         event.classList.add('.active');
    }

return(   
        <div className={`${styles.sidenav}`}>
        <ul>
            <li className={"li"} onClick={(event) => onClickButton(event)}>MENU 1</li>
            <li className={`${styles.li}`} onClick={(event) => onClickButton(event)}>MENU 2</li>
            <li className={`${styles.li}`} onClick={(event) => onClickButton(event)}>MENU 3</li>
            <li className={`${styles.li}`} onClick={(event) => onClickButton(event)}>MENU 4</li>
        </ul>
        </div> 
)
}

export default NewProduct;
