import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";

import styles from './NavBar.module.css';

const NavBar = (props) => {
    const history = useHistory();

    const onClickButton = (event) => {
        let li_elements = document.querySelectorAll('.menu-item')
        for(let x = 0; x < li_elements.length; x++) {
            li_elements[x].classList.remove(`${styles.active}`)
        }
    }

    const handleClick = () =>{
        history.push("/manage-inventory");
    }

    return(
            <ul className={`${styles.sidenav}`}>
                    <li className={`${styles.li} menu-item`} onClick={(event) => onClickButton(event), handleClick} id="manage-products">
                    Manage Products
                    </li>
                    <li className={`${styles.li} menu-item`} onClick={(event) => onClickButton(event), handleClick} id="manage-inventory">
                    Manage Inventory
                    </li>
                    <li className={`${styles.li} menu-item`} onClick={(event) => onClickButton(event), handleClick} id="manage-payments">
                    Manage Payments
                    </li>
                    <li className={`${styles.li} menu-item`} onClick={(event) => onClickButton(event), handleClick} id="manage-accounts">
                    Manage Accounts
                    </li>
            </ul>
    );
}

export default NavBar;
