import React, { useState } from "react";

import Button from '../../UI/Button/Button';
import TextField from '../../UI/TextField/TextField';
import styles from './ProductItem.module.css';

const ProductItem = ({productName, price, stock}) =>{
    const [countProduct, setCountProduct] = useState(0);

    const incrementCount = () =>{
        setCountProduct(countProduct + 1);
    }
    
    const decrementCount = () =>{
        if(countProduct >> 0){
            setCountProduct(countProduct - 1);
        }
        
    }

    return(
        
        <div className={`${styles.div}`}>
            <div className={`${styles.productleft}`}>
            <div className={`${styles.header}`}>
                <h1>{productName}</h1>
                <h2>Remaining: {stock}</h2>
            </div>
            <div className={`${styles.productdetails}`}>
                <div className={`${styles.producttotal}`}>
                <h3>Price</h3>
                <p>P{price}</p>
                </div>
            </div>
            <div className={`${styles.row}`}>
                <button aria-label="Decrement value" className={styles.button} onClick={incrementCount}>-</button>
                <span className={styles.value}>{countProduct}</span>
                <button aria-label="Increment value" className={styles.button}>+</button>
            </div>
            
            <div className={`${styles.productbtns}`}>
                <Button>Add to Cart</Button>
            </div>
            </div>
        </div>
    );

}

export default ProductItem;
