import './App.css';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom'; 

import AdminDashboard from './Components/AdminDashboard/AdminDashboard';
import ProtectedRoute from './Components/Route/ProtectedRoute';
import ProductList from './Components/Product/ViewProducts/ProductList';
import Login from './Components/Login/Login';
import Register from './Components/Register/Register';
import React from 'react';
import { useState } from 'react';
import { useEffect } from 'react';
import Axios from 'axios';


function App() {

  const [Auth, setIsTrue] = useState(false)

  useEffect( async () => {
    let result = await Axios.get("http://localhost:3001/isUserAuth", 
    {headers: {"x-access-token": localStorage.getItem("token")}});

    setIsTrue(result.data.Auth)

  },[])

  return (
    <Router>
      <Switch>
      <React.Fragment>
          <Route exact path="/login" component={Login}/>
          <Route exact path={["/register"]} component={Register}/>
          <Route exact path={["/", "/baligya"]} component={ProductList}/>
          <Route path={"/dashboard"} component={()=> <AdminDashboard Authorized={Auth}/>}/>
      </React.Fragment>
      </Switch>
    </Router>
  );
 }

export default App;
