import React, { useEffect, useState }  from "react";

import styles from './Inventory.module.css';

const Inventory = (props) =>{

    useEffect(() => {
        let ac = document.getElementById('manage-inventory');
        console.log(ac.classList);
        onAddClass(ac)
    },[])

    const onAddClass = (ac) => {
        let li_elements = document.querySelectorAll('.menu-item.active')
        for(let x = 0; x < li_elements.length; x++) {
        li_elements[x].classList.remove(`${styles.active}`)
        }
        ac.classList.add(`${styles.active}`);
    }
    return(
        <div className={`${styles.container}`}>
            Inventory
        </div>
    )
}

export default Inventory;
