import React, {useEffect, useState} from "react";
import Axios from "axios";

import NewProduct from "./NewProduct/NewProduct";
import styles from './Product.module.css';
import ProductItem from "./ViewProducts/ProductItem";


const Product = (props) =>{

    const [products, getProducts] = useState([]);

    useEffect(()=>{
        Axios.get('http://localhost:3001/api/get').then((response)=>{
            getProducts(response.data);
        })
    },[products]);

    const productGetter = (response) =>{
        getProducts(response);
    }

    return( 
        <div className={`${styles.container}`}> 
            <NewProduct getProducts={productGetter}></NewProduct>
            <ul>{products.map((product)=>{
                return(
                    <li style={{display: 'inline-block', marginRight: '5rem'}} key={product.prod_id}>
                    <ProductItem productName={product.prod_name} price={product.price} stock={10}/>
                </li>
                )
                
            })}
            </ul>
        </div>
    )
}

export default Product;
